import React from 'react';
import M from "materialize-css";
import InfiniteScroll from "react-infinite-scroll-component";

const style = {
    height: 30,
    border: "1px solid green",
    margin: 6,
    padding: 8
};

class SignInData extends React.Component {

    
    state = {
        items: Array.from({  length: 25 }),
        hasMore: true
    };


    fetchMoreData = () => {
        if (this.state.items.length >= 500) {
            this.setState({ hasMore: false });

            return
        }

        setTimeout(() => {
            this.setState({
                items: this.state.items.concat(Array.from({ length: 20 }))
            });
        }, 500);
    };

    initialize = () => {
        document.addEventListener('DOMContentLoaded', function() {
            var elems = document.querySelectorAll('select');
            var instance = M.FormSelect.init(elems);
        });
    }

    render() {
        return (
            <>
                <h1>demo: react infinite-scroll-component</h1>
                <div classname="input-field col s6">
                    <label>Data</label>
                    <select>
                        <option value="" disabled selected>Choose your option</option>
                        <option value="1">pure_review</option>
                        <option value="2">pure_extract_review</option>
                        <option value="3">lemmatize_review</option>
                        <option value="4">afinn_review</option>
                        <option value="5">emolex_review</option>
                    </select>
                </div>
                <hr />

                <InfiniteScroll
                    dataLength={this.state.items.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.hasMore}
                    loader={<h4>Loading...</h4>}
                    endMessage={
                        <p style={{ textAlign: "center" }}>
                            <b>Yay! You have seen it all</b>
                        </p>
                    }>
                    
                    {this.state.items.map((i, index) => (
                        <div style={style} key={index}>
                            div - #{index}
                        </div>
                    ))}

                </InfiniteScroll>
            </>
        )
    }
}

export default SignInData;
