import React from 'react';
import InfiniteScroll from "react-infinite-scroll-component";

import '../css/SignInData.css';

const style = {
    height: 30,
    border: "1px solid white",
    margin: 10,
    padding: 2
};

class SignInData extends React.Component {

    
    state = {
        items: Array.from({  length: 30 }),
        hasMore: true
    };


    fetchMoreData = () => {
        if (this.state.items.length >= 500) {
            this.setState({ hasMore: false });

            return
        }

        setTimeout(() => {
            this.setState({
                items: this.state.items.concat(Array.from({ length: 20 }))
            });
        }, 500);
    };


    render() {
        return (
            <>
                <div className="title_box row">
                    <p className="inner_title_box col s10">
                        <h3>pure_review</h3>
                    </p>
                    <p className="radio_box col s2">
                        <label>
                            <input 
                                type="radio"
                                name="data"
                                id="data"
                                value="pure_review"
                                onChange={this.handleInputChange} />
                            <span> </span>
                        </label>
                        <label>
                            <input
                                type="radio"
                                name="data"
                                id="data"
                                value="pure_extract_review"
                                onChange={this.handleInputChange} />
                            <span> </span>
                        </label>
                        <label>
                            <input 
                                type="radio"
                                name="data"
                                id="data"
                                value="lemmatize_review"
                                onChange={this.handleInputChange} />
                            <span> </span>
                        </label>
                        <label>
                            <input
                                type="radio"
                                name="data"
                                id="data"
                                value="afinn_review"
                                onChange={this.handleInputChange} />
                            <span> </span>
                        </label>
                        <label>
                            <input
                                type="radio"
                                name="data"
                                id="data"
                                value="emolex_review"
                                onChange={this.handleInputChange} />
                            <span> </span>
                        </label>
                    </p>
                </div>

                <hr />

                <InfiniteScroll
                    dataLength={this.state.items.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.hasMore}
                    loader={<h4>Loading...</h4>}
                    endMessage={
                        <p style={{ textAlign: "center" }}>
                            <b>Yay! You have seen it all</b>
                        </p>
                    }>
                    
                    {this.state.items.map((i, index) => (
                        <div className="row" style={style} key={index}>
                            <p className="col s1">{index}</p>
                            <p className="col s2">country</p>
                            <p className="col s2">restaurant</p>
                            <p className="col s2">title</p>
                            <p className="col s1">rating</p>
                            <p className="col s2">date</p>
                            <p className="col s2">lang</p>
                        </div>
                    ))}

                </InfiniteScroll>
            </>
        )
    }
}

export default SignInData;
